// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

contract PacaBingo is Ownable, ReentrancyGuard {
    uint256 public constant TICKET_PRICE = 188000000000000 wei; // 0.000188 BNB
    uint256 public constant GAME_DURATION = 10 minutes;
    uint256 public constant ONE_LINE_PERCENTAGE = 30; // 30% of prize pool
    uint256 public constant FULL_HOUSE_PERCENTAGE = 70; // 70% of prize pool

    enum WinType { NONE, ONE_LINE, FULL_HOUSE }

    struct Game {
        uint256 startTime;
        uint256[] calledNumbers;
        mapping(address => uint256[][5]) playerCards;
        mapping(address => WinType) playerWinType;
        bool hasOneLineWinner;
        bool hasFullHouseWinner;
        bool isActive;
        uint256 prizePool;
    }

    uint256 public currentGameId;
    mapping(uint256 => Game) public games;

    event GameStarted(uint256 indexed gameId, uint256 startTime);
    event NumberCalled(uint256 indexed gameId, uint256 number);
    event TicketPurchased(uint256 indexed gameId, address indexed player);
    event Winner(uint256 indexed gameId, address indexed winner, uint256 prize, WinType winType);

    constructor() {
        startNewGame();
    }

    function startNewGame() public onlyOwner {
        require(!games[currentGameId].isActive, "Current game still active");
        currentGameId++;

        Game storage game = games[currentGameId];
        game.startTime = block.timestamp;
        game.isActive = true;
        game.hasOneLineWinner = false;
        game.hasFullHouseWinner = false;

        emit GameStarted(currentGameId, block.timestamp);
    }

    function buyTicket(uint256[][5] memory numbers) external payable nonReentrant {
        require(msg.value == TICKET_PRICE, "Incorrect ticket price");
        require(games[currentGameId].isActive, "No active game");
        require(block.timestamp < games[currentGameId].startTime + GAME_DURATION, "Game already in progress");

        // Validate numbers
        for (uint i = 0; i < 5; i++) {
            require(numbers[i].length == 5, "Invalid card format");
            for (uint j = 0; j < 5; j++) {
                uint256 num = numbers[i][j];
                require(num > 0 && num <= 75, "Number out of range");
            }
        }

        games[currentGameId].playerCards[msg.sender] = numbers;
        games[currentGameId].prizePool += msg.value;
        games[currentGameId].playerWinType[msg.sender] = WinType.NONE;

        emit TicketPurchased(currentGameId, msg.sender);
    }

    function callNumber(uint256 number) external onlyOwner {
        require(games[currentGameId].isActive, "No active game");
        require(number > 0 && number <= 75, "Invalid number");

        games[currentGameId].calledNumbers.push(number);
        emit NumberCalled(currentGameId, number);
    }

    function claimWin() external nonReentrant {
        Game storage game = games[currentGameId];
        require(game.isActive, "No active game");
        require(game.playerWinType[msg.sender] == WinType.NONE, "Already claimed");

        WinType winType = checkWin(msg.sender);
        require(winType != WinType.NONE, "No winning pattern found");

        if (winType == WinType.ONE_LINE) {
            require(!game.hasOneLineWinner, "One line prize already claimed");
            game.hasOneLineWinner = true;
        } else if (winType == WinType.FULL_HOUSE) {
            require(!game.hasFullHouseWinner, "Full house prize already claimed");
            game.hasFullHouseWinner = true;
        }

        game.playerWinType[msg.sender] = winType;
        uint256 prize = calculatePrize(winType, game.prizePool);

        if (game.hasOneLineWinner && game.hasFullHouseWinner) {
            game.isActive = false;
        }

        payable(msg.sender).transfer(prize);
        emit Winner(currentGameId, msg.sender, prize, winType);
    }

    function calculatePrize(WinType winType, uint256 totalPrizePool) internal pure returns (uint256) {
        if (winType == WinType.ONE_LINE) {
            return (totalPrizePool * ONE_LINE_PERCENTAGE) / 100;
        } else if (winType == WinType.FULL_HOUSE) {
            return (totalPrizePool * FULL_HOUSE_PERCENTAGE) / 100;
        }
        return 0;
    }

    function checkWin(address player) internal view returns (WinType) {
        Game storage game = games[currentGameId];
        uint256[][5] memory card = game.playerCards[player];
        uint256[] memory called = game.calledNumbers;

        // Check for full house
        bool isFullHouse = true;
        for (uint i = 0; i < 5 && isFullHouse; i++) {
            for (uint j = 0; j < 5; j++) {
                bool numberFound = false;
                for (uint k = 0; k < called.length; k++) {
                    if (card[i][j] == called[k]) {
                        numberFound = true;
                        break;
                    }
                }
                if (!numberFound) {
                    isFullHouse = false;
                    break;
                }
            }
        }

        if (isFullHouse && !game.hasFullHouseWinner) {
            return WinType.FULL_HOUSE;
        }

        // Check for one line (rows)
        for (uint i = 0; i < 5; i++) {
            bool rowComplete = true;
            for (uint j = 0; j < 5; j++) {
                bool numberFound = false;
                for (uint k = 0; k < called.length; k++) {
                    if (card[i][j] == called[k]) {
                        numberFound = true;
                        break;
                    }
                }
                if (!numberFound) {
                    rowComplete = false;
                    break;
                }
            }
            if (rowComplete && !game.hasOneLineWinner) return WinType.ONE_LINE;
        }

        // Check for one line (columns)
        for (uint i = 0; i < 5; i++) {
            bool colComplete = true;
            for (uint j = 0; j < 5; j++) {
                bool numberFound = false;
                for (uint k = 0; k < called.length; k++) {
                    if (card[j][i] == called[k]) {
                        numberFound = true;
                        break;
                    }
                }
                if (!numberFound) {
                    colComplete = false;
                    break;
                }
            }
            if (colComplete && !game.hasOneLineWinner) return WinType.ONE_LINE;
        }

        return WinType.NONE;
    }

    function getCalledNumbers() external view returns (uint256[] memory) {
        return games[currentGameId].calledNumbers;
    }

    function getGameInfo() external view returns (
        bool isActive,
        uint256 startTime,
        uint256 prizePool,
        uint256[] memory calledNumbers,
        bool hasOneLineWinner,
        bool hasFullHouseWinner
    ) {
        Game storage game = games[currentGameId];
        return (
            game.isActive,
            game.startTime,
            game.prizePool,
            game.calledNumbers,
            game.hasOneLineWinner,
            game.hasFullHouseWinner
        );
    }
}
