// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "./IERC20.sol";
import "./IStaking.sol";

contract PacaBingo is Ownable, ReentrancyGuard {
    IERC20 public usdt;
    IStaking public stakingContract;
    uint256 public ticketPrice = 1 * 10**18; // 1 USDT (18 decimals)
    uint256 public constant GAME_DURATION = 10 minutes;
    uint256 public oneLinePercentage = 30;
    uint256 public fullHousePercentage = 70;
    address public admin;

    enum WinType { NONE, ONE_LINE, FULL_HOUSE }
    enum GameMode { SOLO, GROUP_5, GROUP_10, GROUP_20 }

    struct GameConfig {
        uint256 minPlayers;
        uint256 maxPlayers;
        uint256 ticketPrice;
        bool active;
    }

    struct Game {
        uint256 startTime;
        uint256[] calledNumbers;
        mapping(address => uint256[][5]) playerCards;
        mapping(address => WinType) playerWinType;
        bool hasOneLineWinner;
        bool hasFullHouseWinner;
        bool isActive;
        uint256 prizePool;
        GameMode mode;
        address[] players;
        uint256 minPlayers;
        uint256 maxPlayers;
    }

    mapping(GameMode => GameConfig) public gameModeConfigs;
    uint256 public currentGameId;
    mapping(uint256 => Game) public games;
    mapping(address => uint256) public playerCurrentGame;

    event GameStarted(uint256 indexed gameId, uint256 startTime, GameMode mode);
    event NumberCalled(uint256 indexed gameId, uint256 number);
    event TicketPurchased(uint256 indexed gameId, address indexed player, GameMode mode);
    event Winner(uint256 indexed gameId, address indexed winner, uint256 prize, WinType winType);
    event WinningsStaked(uint256 indexed gameId, address indexed winner, uint256 amount);
    event TicketPriceUpdated(GameMode mode, uint256 newPrice);
    event GameModeConfigUpdated(GameMode mode, uint256 minPlayers, uint256 maxPlayers, uint256 ticketPrice);
    event PrizePoolPercentagesUpdated(uint256 oneLinePercent, uint256 fullHousePercent);

    constructor(address _usdtAddress, address _stakingAddress) {
        admin = msg.sender;
        usdt = IERC20(_usdtAddress);
        stakingContract = IStaking(_stakingAddress);

        // Initialize game mode configs with USDT prices
        gameModeConfigs[GameMode.SOLO] = GameConfig(1, 1, 1 * 10**18, true);        // 1 USDT
        gameModeConfigs[GameMode.GROUP_5] = GameConfig(5, 5, 2 * 10**18, true);     // 2 USDT
        gameModeConfigs[GameMode.GROUP_10] = GameConfig(10, 10, 5 * 10**18, true);  // 5 USDT
        gameModeConfigs[GameMode.GROUP_20] = GameConfig(20, 20, 10 * 10**18, true); // 10 USDT
    }

    modifier onlyAdminOrOwner() {
        require(msg.sender == admin || msg.sender == owner(), "Not authorized");
        _;
    }

    function updateGameModeConfig(
        GameMode mode,
        uint256 minPlayers,
        uint256 maxPlayers,
        uint256 _ticketPrice,
        bool active
    ) external onlyAdminOrOwner {
        require(minPlayers > 0 && maxPlayers >= minPlayers, "Invalid player counts");
        require(_ticketPrice > 0, "Invalid ticket price");

        gameModeConfigs[mode] = GameConfig(minPlayers, maxPlayers, _ticketPrice, active);
        emit GameModeConfigUpdated(mode, minPlayers, maxPlayers, _ticketPrice);
    }

    function startNewGame(GameMode mode) public {
        require(gameModeConfigs[mode].active, "Game mode not active");
        currentGameId++;

        Game storage game = games[currentGameId];
        game.startTime = block.timestamp;
        game.isActive = true;
        game.mode = mode;
        game.minPlayers = gameModeConfigs[mode].minPlayers;
        game.maxPlayers = gameModeConfigs[mode].maxPlayers;

        emit GameStarted(currentGameId, block.timestamp, mode);
    }

    function buyTicket(uint256[][5] memory numbers, GameMode mode) external nonReentrant {
        GameConfig memory config = gameModeConfigs[mode];
        require(config.active, "Game mode not active");
        require(playerCurrentGame[msg.sender] == 0, "Already in a game");

        uint256 gameId = findOrCreateGame(mode);
        Game storage game = games[gameId];

        require(game.players.length < game.maxPlayers, "Game is full");
        require(!hasGameStarted(gameId), "Game already in progress");

        // Validate numbers
        for (uint i = 0; i < 5; i++) {
            require(numbers[i].length == 5, "Invalid card format");
            for (uint j = 0; j < 5; j++) {
                require(numbers[i][j] >= 1 && numbers[i][j] <= 75, "Numbers must be between 1 and 75");
            }
        }

        // Transfer USDT from player to contract
        require(usdt.transferFrom(msg.sender, address(this), config.ticketPrice), "USDT transfer failed");

        game.playerCards[msg.sender] = numbers;
        game.players.push(msg.sender);
        game.prizePool += config.ticketPrice;
        playerCurrentGame[msg.sender] = gameId;

        emit TicketPurchased(gameId, msg.sender, mode);

        // Auto-start game if max players reached
        if (game.players.length == game.maxPlayers) {
            startGamePlay(gameId);
        }
    }

    function findOrCreateGame(GameMode mode) internal returns (uint256) {
        // Look for an existing game that hasn't started
        for (uint256 i = currentGameId; i > 0 && i > currentGameId - 10; i--) {
            if (games[i].isActive && 
                games[i].mode == mode && 
                !hasGameStarted(i) &&
                games[i].players.length < games[i].maxPlayers) {
                return i;
            }
        }

        // Create new game if none found
        startNewGame(mode);
        return currentGameId;
    }

    function hasGameStarted(uint256 gameId) internal view returns (bool) {
        Game storage game = games[gameId];
        return game.calledNumbers.length > 0;
    }

    function startGamePlay(uint256 gameId) internal {
        Game storage game = games[gameId];
        require(game.players.length >= game.minPlayers, "Not enough players");
        game.startTime = block.timestamp;
        // Initial number call will be done in a separate function
    }

    function callNumber(uint256 number, uint256 gameId) external onlyAdminOrOwner {
        Game storage game = games[gameId];
        require(game.isActive, "No active game");
        require(number >= 1 && number <= 75, "Invalid number");

        game.calledNumbers.push(number);
        emit NumberCalled(gameId, number);
    }

    function claimPrize(uint256 gameId) external nonReentrant {
        Game storage game = games[gameId];
        require(game.isActive, "Game not active");
        require(game.startTime + GAME_DURATION <= block.timestamp, "Game still in progress");

        WinType winType = checkWin(msg.sender, gameId);
        require(winType != WinType.NONE, "No win");
        require(game.playerWinType[msg.sender] == WinType.NONE, "Already claimed");

        uint256 prize;
        if (winType == WinType.ONE_LINE && !game.hasOneLineWinner) {
            prize = (game.prizePool * oneLinePercentage) / 100;
            game.hasOneLineWinner = true;
        } else if (winType == WinType.FULL_HOUSE && !game.hasFullHouseWinner) {
            prize = (game.prizePool * fullHousePercentage) / 100;
            game.hasFullHouseWinner = true;
        } else {
            revert("Prize already claimed for this win type");
        }

        game.playerWinType[msg.sender] = winType;

        // First approve staking contract to spend USDT
        require(usdt.approve(address(stakingContract), prize), "Failed to approve staking contract");

        // Stake the winnings directly for the winner
        require(stakingContract.stakeFor(msg.sender, prize), "Failed to stake winnings");

        emit Winner(gameId, msg.sender, prize, winType);
        emit WinningsStaked(gameId, msg.sender, prize);

        if (game.hasOneLineWinner && game.hasFullHouseWinner) {
            game.isActive = false;
        }
    }

    function calculatePrize(WinType winType, uint256 totalPrizePool) internal view returns (uint256) {
        if (winType == WinType.ONE_LINE) {
            return (totalPrizePool * oneLinePercentage) / 100;
        } else if (winType == WinType.FULL_HOUSE) {
            return (totalPrizePool * fullHousePercentage) / 100;
        }
        return 0;
    }

    function checkWin(address player, uint256 gameId) internal view returns (WinType) {
        Game storage game = games[gameId];
        uint256[][5] memory card = game.playerCards[player];
        uint256[] memory called = game.calledNumbers;

        // Check for full house
        bool isFullHouse = true;
        for (uint i = 0; i < 5 && isFullHouse; i++) {
            for (uint j = 0; j < 5; j++) {
                bool numberFound = false;
                for (uint k = 0; k < called.length; k++) {
                    if (card[i][j] == called[k]) {
                        numberFound = true;
                        break;
                    }
                }
                if (!numberFound) {
                    isFullHouse = false;
                    break;
                }
            }
        }

        if (isFullHouse && !game.hasFullHouseWinner) {
            return WinType.FULL_HOUSE;
        }

        // Check for one line (rows)
        for (uint i = 0; i < 5; i++) {
            bool rowComplete = true;
            for (uint j = 0; j < 5; j++) {
                bool numberFound = false;
                for (uint k = 0; k < called.length; k++) {
                    if (card[i][j] == called[k]) {
                        numberFound = true;
                        break;
                    }
                }
                if (!numberFound) {
                    rowComplete = false;
                    break;
                }
            }
            if (rowComplete && !game.hasOneLineWinner) return WinType.ONE_LINE;
        }

        // Check for one line (columns)
        for (uint i = 0; i < 5; i++) {
            bool colComplete = true;
            for (uint j = 0; j < 5; j++) {
                bool numberFound = false;
                for (uint k = 0; k < called.length; k++) {
                    if (card[j][i] == called[k]) {
                        numberFound = true;
                        break;
                    }
                }
                if (!numberFound) {
                    colComplete = false;
                    break;
                }
            }
            if (colComplete && !game.hasOneLineWinner) return WinType.ONE_LINE;
        }

        return WinType.NONE;
    }

    function getCalledNumbers(uint256 gameId) external view returns (uint256[] memory) {
        return games[gameId].calledNumbers;
    }

    function getGameInfo(uint256 gameId) external view returns (
        bool isActive,
        uint256 startTime,
        uint256 prizePool,
        uint256[] memory calledNumbers,
        bool hasOneLineWinner,
        bool hasFullHouseWinner
    ) {
        Game storage game = games[gameId];
        return (
            game.isActive,
            game.startTime,
            game.prizePool,
            game.calledNumbers,
            game.hasOneLineWinner,
            game.hasFullHouseWinner
        );
    }
}
